Alfresco.util.blog={};Alfresco.util.blog.generatePublishingRestURL=function generatePublishingRestURL(c,b,a){return YAHOO.lang.substitute(Alfresco.constants.PROXY_URI+"api/blog/post/site/{site}/{container}/{postId}/publishing",{site:c,container:b,postId:a})};Alfresco.util.blog.generateBlogPostViewUrl=function generateBlogPostViewUrl(d,b,a){var c=YAHOO.lang.substitute(Alfresco.constants.URL_PAGECONTEXT+"site/{site}/blog-postview?postId={postId}&listViewLinkBack=true",{site:d,postId:a});return c};Alfresco.util.blog.generatePostStatusLabel=function generatePostStatusLabel(b,c){if(c.isDraft){return"("+b._msg("status.draft")+")"}else{if(c.isUpdated||c.isPublished){var a="";if(c.isUpdated){a+="("+b._msg("status.updated")+") "}if(c.isPublished){if(c.outOfDate){return a+"("+b._msg("status.published.outofsync")+")"}else{return a+"("+b._msg("status.published")+")"}}else{return a}}else{return""}}};Alfresco.util.blog.generateBlogPostActions=function generateBlogPostActions(b,c,a){var d="";d+='<div class="nodeEdit">';if(c.permissions.edit){d+="<"+a+' class="onEditBlogPost"><a href="#" class="blogpost-action-link-'+a+'"><span>'+b._msg("action.edit")+"</span></a></"+a+">"}if(c.permissions["delete"]){d+="<"+a+' class="onDeleteBlogPost"><a href="#" class="blogpost-action-link-'+a+'"><span>'+b._msg("action.delete")+"</span></a></"+a+">"}d+="</div>";return d};